mapper.select:
SELECT a.id, a.assessor_id AS assessorId, a.assessed_id AS assessedId, a.rule_id AS ruleId, a.total_score AS totalScore, a.opinion, a.month, a.day, a.sort, a.is_valid AS isValid, a.update_user_id AS updateUserId, a.update_time AS update_time, a.create_user_id AS createUserId, a.create_time AS createTime, a.remark , b.`name` AS ret FROM `t_ws_kpi_score_ret` a LEFT JOIN `t_ws_kpi_ret_level` b ON a.`total_score` >= b.`lowest_score` AND a.`total_score` <= b.`highest_score` WHERE 1=1
AND a.month = #{month}
AND a.assessor_id = #{assessorId} AND a.`assessed_id` = #{assessedId}
